import React from "react";
import "../../index.css";
import AppContext from "../../context"
import { BrowserRouter, Route, Switch } from 'react-router-dom';
import ArticlesView from "../ArticlesView/ArticlesView";
import TwittersView from "../TwittersView/TwittersView";
import NotesView from "../NotesView/NotesView";
import Navigation from "../../components/Header/HeaderNavigation";
import Header from "../../components/Header/Header";
import Modal from "../../components/Modal/Modal";



const initialStateItems = [
  {
    image: "https://pbs.twimg.com/profile_images/906557353549598720/oapgW_Fp.jpg",
    name: "Dan Abramov",
    description: "React core member",
    twitterLink: "https://twitter.com/dan_abramov"
  }
];

class Root extends React.Component {
  state = {
    items: [...initialStateItems],
    isModalOpen: false,
    name: 'Michał',
  };

  addItem = e => {
    e.preventDefault();

    const newItem = {
      name: e.target[0].value,
      twitterLink: e.target[1].value,
      image: e.target[2].value,
      description: e.target[3].value
    };

    this.setState(prevState => ({
      items: [...prevState.items, newItem]
    }));

    e.target.reset();
  };

  openModal = () => {
    this.setState({
      isModalOpen: true,
    })
  }

  closeModal = () => {
    this.setState({
      isModalOpen: false,
    })
  }

  render() {
    const { isModalOpen } = this.state;
    return (
      <BrowserRouter>
        <AppContext.Provider value={this.state.name}>
          <Header openModalFn={this.openModal}></Header>
          <h1>Mały Kotek</h1>
          <Switch>
            <Route exact path="/" component={TwittersView} />
            <Route path="/articles" component={ArticlesView} />
            <Route path="/notes" component={NotesView} />
          </Switch>
          { isModalOpen && <Modal closeModalFn={this.closeModal}/>}
        </AppContext.Provider>
      </BrowserRouter>
    );
  }
}

export default Root;
