import React from "react";
import AppContext from "../../context";
import styles from "./Form.module.scss";
import Input from "../Input/Input";
import Button from "../Button/Button";
import Title from "../Title/Title";

const types = {
  twitter: "twitter",
  article: "article",
  note: "note",
};

const descriptions = {
  twitter: "favourite Twitter account",
  article: "Article",
  note: "Note",
};

class Form extends React.Component {
  state = {
    activeOption: types.twitter,
    title: '',
    link: '',
    image: '',
    description: '',
  };

  handleRadioButtonChange = (type) => {
    this.setState({
      activeOption: type,
    });
  };

  handleInputChange = e => {
    console.log(e.target.name);
  };

  render() {
    const { activeOption } = this.state;

    return (
      <AppContext.Consumer>
        {(context) => (
          <div className={styles.wrapper}>
            <Title>Add new {descriptions[this.state.activeOption]}</Title>
            <form
              autoComplete="off"
              className={styles.form}
              onSubmit={context.addItem}
            >
              <div className={styles.wrapperInput}>
                <input
                  className={styles.input}
                  id={types.twitter}
                  type="radio"
                  checked={this.state.activeOption === types.twitter}
                  onChange={() => this.handleRadioButtonChange(types.twitter)}
                />
                <label for={types.article}>Twitter</label>
                <input
                  className={styles.input}
                  id={types.article}
                  type="radio"
                  checked={this.state.activeOption === types.article}
                  onChange={() => this.handleRadioButtonChange(types.article)}
                />
                <label for={types.article}>Article</label>
                <input
                  className={styles.input}
                  id={types.note}
                  type="radio"
                  checked={this.state.activeOption === types.note}
                  onChange={() => this.handleRadioButtonChange(types.note)}
                />
                <label for={types.article}>Note</label>
              </div>
              <Input
              onChange={this.handleInputChange} name="name" label="Name" maxLength={30} />
              <Input
              onChange={this.handleInputChange} name="link" label="Twitter link" />
              <Input
              onChange={this.handleInputChange} name="image" label="Image" />
              <Input
              onChange={this.handleInputChange} tag="textarea" name="description" label="Description" />
              <Button>add new item</Button>
            </form>
          </div>
        )}
      </AppContext.Consumer>
    );
  }
}

export default Form;
